import React from 'react';
import { useHistory, Link } from 'react-router-dom';
import './post-styles.css';
import {
  TwitterShareButton,
  FacebookShareButton,
  LinkedinShareButton,
  WhatsappShareButton,
} from 'react-share';
import {
  EmailIcon,
  FacebookIcon,
  FacebookMessengerIcon,
  HatenaIcon,
  InstapaperIcon,
  LineIcon,
  LinkedinIcon,
  LivejournalIcon,
  MailruIcon,
  OKIcon,
  PinterestIcon,
  PocketIcon,
  RedditIcon,
  TelegramIcon,
  TumblrIcon,
  TwitterIcon,
  ViberIcon,
  VKIcon,
  WeiboIcon,
  WhatsappIcon,
  WorkplaceIcon,
} from 'react-share';

export default function Post({
  id,
  url,
  title,
  date,
  onDelete,
  author,
  category,
}) {
  const history = useHistory();

  function handleClick() {
    history.push(`/post/${id}`);
  }

  function handleDelete() {
    onDelete(id);
  }

  return (
    <li id='postAndButton'>
      <div id='postPreview' onClick={handleClick}>
        <img src={url} alt='thumbnail' />
        <h2>{title}</h2>
        <h3>By : "{author}"</h3>
        <h4>Category : {category}</h4>
        <em>{date}</em>
      </div>
      <span id='deleteBtn' onClick={handleDelete}>
        <img
          src='https://icons-for-free.com/iconfiles/png/512/close+closecancelsquare+cross+delete+remove+square+icon-1320185734508070818.png'
          alt='Delete post'
        />
      </span>
      
      
      <TwitterShareButton
        url='https://www.twitter.com/'
        title={title}
        className='Demo__some-network__share-button'
      >
        <TwitterIcon size={32} round />
      </TwitterShareButton>
    </li>
  );
}
